 1	package com.metenkanich.fastfoodkiosk.persistence.connection;
 2	
 3	import com.zaxxer.hikari.HikariConfig;
 4	import com.zaxxer.hikari.HikariDataSource;
 5	import javax.sql.DataSource;
 6	
 7	public class DatabaseConnection {
 8	
 9	 private static final String JDBC_URL = "jdbc:postgresql://localhost:5432/fast_food_kiosk";
 10	 private static final String USERNAME = "postgres";
 11	 private static final String PASSWORD = "";
 12	
 13	 private static DatabaseConnection instance;
 14	 private static HikariDataSource dataSource;
 15	
 16	 private DatabaseConnection() {
 17	 }
 18	
 19	 public static synchronized DatabaseConnection getInstance() {
 20	 if (instance == null) {
 21	 instance = new DatabaseConnection();
 22	 }
 23	 return instance;
 24	 }
 25	
 26	 public static void initializeDataSource() {
 27	 HikariConfig config = new HikariConfig();
 28	 config.setJdbcUrl(JDBC_URL);
 29	 config.setUsername(USERNAME);
 30	 config.setPassword(PASSWORD);
 31	 dataSource = new HikariDataSource(config);
 32	 }
 33	
 34	 public DataSource getDataSource() {
 35	 if (dataSource == null) {
 36	 initializeDataSource(); // Ініціалізація джерела даних
 37	 }
 38	 return dataSource;
 39	 }
 40	
 41	 public void closePool() {
 42	 if (dataSource != null) {
 43	 dataSource.close();
 44	 }
 45	 }
 46	}
