 23	 @Override
 24	 public MenuItem findById(UUID id) throws EntityNotFoundException {
 25	 String query = "SELECT * FROM MenuItems WHERE item_id = ?";
 26	 try (Connection connection = dataSource.getConnection();
 27	 PreparedStatement preparedStatement = connection.prepareStatement(query)) {
 28	 preparedStatement.setString(1, id.toString());
 29	 try (ResultSet resultSet = preparedStatement.executeQuery()) {
 30	 if (resultSet.next()) {
 31	 return mapToMenuItem(resultSet);
 32	 } else {
 33	 throw new EntityNotFoundException("Елемент меню з ID " + id + " не знайдено");
 34	 }
 35	 }
 36	 } catch (SQLException e) {
 37	 throw new EntityNotFoundException("Помилка під час пошуку елемента меню з ID " + id, e);
 38	 }
 39	 }
 40	
 41	 @Override
 42	 public MenuItem findByName(String name) throws EntityNotFoundException {
 43	 String query = "SELECT * FROM MenuItems WHERE name = ?";
 44	 try (Connection connection = dataSource.getConnection();
 45	 PreparedStatement preparedStatement = connection.prepareStatement(query)) {
 46	 preparedStatement.setString(1, name);
 47	 try (ResultSet resultSet = preparedStatement.executeQuery()) {
 48	 if (resultSet.next()) {
 49	 return mapToMenuItem(resultSet);
 50	 } else {
 51	 throw new EntityNotFoundException("Елемент меню з назвою " + name + " не знайдено");
 52	 }
 53	 }
 54	 } catch (SQLException e) {
 55	 throw new EntityNotFoundException("Помилка під час пошуку елемента меню з назвою " + name, e);
 56	 }
 57	 }
 58	
 59	 @Override
 60	 public List<MenuItem> findAll() {
 61	 List<MenuItem> items = new ArrayList<>();
 62	 String query = "SELECT * FROM MenuItems";
 63	 try (Connection connection = dataSource.getConnection();
 64	 PreparedStatement preparedStatement = connection.prepareStatement(query);
 65	 ResultSet resultSet = preparedStatement.executeQuery()) {
 66	 while (resultSet.next()) {
 67	 items.add(mapToMenuItem(resultSet));
 68	 }
 69	 } catch (SQLException e) {
 70	 e.printStackTrace();
 71	 }
 72	 return items;
 73	 }
 74	
 75	 @Override
 76	 public List<MenuItem> findByCategoryId(UUID categoryId) {
 77	 List<MenuItem> items = new ArrayList<>();
 78	 String query = "SELECT m.* FROM MenuItems m JOIN ItemesCategories ic ON m.item_id = ic.item_id WHERE ic.category_id = ?";
 79	 try (Connection connection = dataSource.getConnection();
 80	 PreparedStatement preparedStatement = connection.prepareStatement(query)) {
 81	 preparedStatement.setString(1, categoryId.toString());
 82	 try (ResultSet resultSet = preparedStatement.executeQuery()) {
 83	 while (resultSet.next()) {
 84	 items.add(mapToMenuItem(resultSet));
 85	 }
 86	 }
 87	 } catch (SQLException e) {
 88	 e.printStackTrace();
 89	 }
 90	 return items;
 91	 }
