 112	 public MenuItem save(MenuItem menuItem) {
 113	 String query = menuItem.itemId() == null
 114	 ? "INSERT INTO MenuItems (item_id, name, description, price, calories, image, ingredients) VALUES (?, ?, ?, ?, ?, ?, ?)"
 115	 : "UPDATE MenuItems SET name = ?, description = ?, price = ?, calories = ?, image = ?, ingredients = ? WHERE item_id = ?";
 116	 try (Connection connection = dataSource.getConnection();
 117	 PreparedStatement preparedStatement = connection.prepareStatement(query)) {
 118	 UUID id = menuItem.itemId() == null ? UUID.randomUUID() : menuItem.itemId();
 119	 int index = 1;
 120	 if (menuItem.itemId() == null) {
 121	 preparedStatement.setString(index++, id.toString());
 122	 }
