 219	 public void addToCart(MenuItem item, int quantity) {
 220	 User currentUser = AuthenticatedUser.getInstance().getCurrentUser();
 221	 if (currentUser == null) {
 222	 if (errorLabel != null) {
 223	 errorLabel.setText("Будь ласка, увійдіть у систему");
 224	 }
 225	 return;
 226	 }
 227	 if (quantity <= 0) {
 228	 if (errorLabel != null) {
 229	 errorLabel.setText("Вкажіть коректну кількість");
 230	 }
 231	 return;
 232	 }
 233	 double subtotal = item.price() * quantity;
 234	 Cart cartItem = new Cart(
 235	 UUID.randomUUID(),
 236	 currentUser.id(),
 237	 item.itemId(),
 238	 quantity,
 239	 subtotal,
 240	 false
 241	 );
 242	 Cart savedCartItem = cartRepository.create(cartItem);
 243	 if (savedCartItem != null) {
 244	 loadMenuItems(); // Оновити відображення
 245	 } else {
 246	 if (errorLabel != null) {
 247	 errorLabel.setText("Помилка додавання до кошика");
 248	 }
 249	 }
 250	 }
